import React, { useState } from 'react';
import oca from '..//assets/images/oca.jpeg';
import moca from '..//assets/images/moca.jpeg';
import toca from '..//assets/images/toca.jpeg';

const images = [oca, moca, toca];

export default function Image() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div>
      <img style={styles.reSize} src={images[currentIndex]} alt="carousel" />
      <div>
        <button onClick={goToPrevious}>Previous</button>
        <button onClick={goToNext}>Next</button>
      </div>
    </div>
  );
}

const styles = {
  reSize: {
    width: 320,
    height: 440,
    paddingTop: 50,
  },
};
