import React, { useState } from 'react';
import oca from '..//assets/images/oca.jpeg';
import moca from '..//assets/images/moca.jpeg';
import toca from '..//assets/images/toca.jpeg';

const images = [oca, moca, toca];

export default function Image() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div style={styles.container}>
      <img style={styles.image} src={images[currentIndex]} alt="carousel" />
      <div style={styles.buttons}>
        <button style={styles.button} onClick={goToPrevious}>←</button>
        <button style={styles.button} onClick={goToNext}>→</button>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
  },
  image: {
    maxWidth: '100%',
    maxHeight: '100%',
    width: 'auto',
    height: '100%',
  },
  buttons: {
    marginTop: '20px',
  },
  button: {
    fontSize: '24px',
    margin: '0 10px',
    cursor: 'pointer',
  },
};
