    const fullname = firstName + '' + lastName   /* Concatena el nombre y el apellido */

    function handleFirstNameChange(e){    /* Función para manejar cambios en el nombre */
        selfFirstName(e.target.value); 


    }

    function handleLastNameChange(e){   /* Función para manejar cambios en el apellido */
        selfLastName(e.target.value);
    }



    return(
        <>

        <h2>SignUp</h2>   
        <form action="" class="form">   {/* Inicio del formulario */}

        <label htmlFor="firstName">First Name : </label>  /* Etiqueta para el campo de nombre */
        <input type="text" value={firstName} onChange={handleFirstNameChange}/> /* Campo de entrada para el nombre */

        <label htmlFor="lasName">Last Name : </label>  /* Etiqueta para el campo de apellido */
        <input type="text" value={lastName} onChange={handleLastNameChange}/> /* Campo de entrada para el apellido */

        </form>

        <p> Welcome : <strong>{fullname}</strong></p> /* Muestra el nombre completo */

        </>
    );
}
