import React, { useState } from 'react';
import oca from '..//assets/images/oca.jpeg';
import moca from '..//assets/images/moca.jpeg';
import toca from '..//assets/images/toca.jpeg';

const images = [oca, moca, toca];

export default function Image() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div style={styles.container}>
      <div style={styles.arrowLeft} onClick={goToPrevious}>←</div>
      <img style={styles.image} src={images[currentIndex]} alt="carousel" />
      <div style={styles.arrowRight} onClick={goToNext}>→</div>
    </div>
  );
}

const styles = {
  container: {
    position: 'relative',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100vw',
    height: '70vh', // Ajusta la altura según sea necesario
  },
  image: {
    maxWidth: '100%',
    maxHeight: '100%',
    width: 'auto',
    height: 'auto',
  },
  arrowLeft: {
    position: 'absolute',
    top: '50%',
    left: '10px', // Ajusta la distancia del borde izquierdo
    transform: 'translateY(-50%)',
    fontSize: '24px',
    cursor: 'pointer',
  },
  arrowRight: {
    position: 'absolute',
    top: '50%',
    right: '10px', // Ajusta la distancia del borde derecho
    transform: 'translateY(-50%)',
    fontSize: '24px',
    cursor: 'pointer',
  },
};
