import { useState, useEffect } from 'react';
import '../App.css';

const url = "https://api.coindesk.com/v1/bpi/currentprice.json"; // URL de la API de CoinDesk

export default function Btc2() {
  const [data, setData] = useState(null); // Estado para almacenar los datos de la API
  const [isLoading, setIsLoading] = useState(true); // Estado para el indicador de carga
  const [error, setError] = useState(null); // Estado para manejar errores

  useEffect(() => {
    // Función para obtener datos de la API
    fetch(url)
      .then(response => response.json())
      .then(result => {
        setIsLoading(false);
        setData(result);
      })
      .catch(error => {
        setIsLoading(false);
        setError(error);
      });
  }, []);

  // Función para mostrar el contenido dependiendo del estado de carga y error
  const getContent = () => {
    if (isLoading) { // Si está cargando
      return (
        <div className="App">
          <h4>Loading Data ...</h4> {/* Mensaje de carga */}
          <progress value={null} /> {/* Indicador de progreso */}
        </div>
      );
    }

    if (error) { // Si hay un error
      return <h4>Error</h4>; {/* Mensaje de error */}
    }

    // Si no hay errores ni carga, muestra los datos de la API
    return (
      <div className="App">
        <h1>BTC to USD | EUR | GBP</h1> {/* Título */}
        <h3>BTC to USD</h3> {/* Subtítulo */}
        {/* Tabla de datos */}
        <div className="d-flex justify-content-center">
          <div className="col-8">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>RATE</th>
                  <th>RATE FLOAT</th>
                  <th>DESCRIPTION</th>
                  <th>UPDATE</th>
                </tr>
              </thead>
              <tbody>
                {/* Fila de datos para USD */}
                <tr>
                  <td>{data["bpi"]["USD"].rate}</td>
                  <td>{data["bpi"]["USD"].rate_float}</td>
                  <td>{data["bpi"]["USD"].description}</td>
                  <td>{data["time"].updated}</td>
                </tr>
                {/* Fila de datos para EUR */}
                <tr>
                  <td>{data["bpi"]["EUR"].rate}</td>
                  <td>{data["bpi"]["EUR"].rate_float}</td>
                  <td>{data["bpi"]["EUR"].description}</td>
                  <td>{data["time"].updated}</td>
                </tr>
                {/* Fila de datos para GBP */}
                <tr>
                  <td>{data["bpi"]["GBP"].rate}</td>
                  <td>{data["bpi"]["GBP"].rate_float}</td>
                  <td>{data["bpi"]["GBP"].description}</td>
                  <td>{data["time"].updated}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      {getContent()} {/* Renderiza el contenido */}
    </div>
  );
}
